const MongoClient = require('mongodb').MongoClient;

const User_Schema = MongoClient.Schema(
    {
        user_name:
            {
                type: String,
                require: true
            },
        user_address:
            {
                type: String,
                require: true
            },
        payment_history:
            {
                type: Array(Array(String)),
                require: true
            }


    });

const UserAccount = module.exports = MongoClient.model("UserAccount", User_Schema);