const MongoClient = require('mongodb').MongoClient;

const Orders_schema = MongoClient.Schema(
    {
        items:
            {
                type:Array(String),
                require: true
            },
        total_amount:
            {
                type:String,
                require:true
            },
        Address:
            {
                type:String,
                require:true
            }
    });

module.exports.Orders = MongoClient.model("UserAccount", Orders_schema);